
using System.Text.RegularExpressions;

class Program
{
    static void Main(string[] args)
    {
        var allWordsRegex = new Regex(@"\w");
        var DzRegex = new Regex(@"\w{3,20}");

        var allWords = allWordsRegex.Matches(Kobzar.text);
        var DzWords = DzRegex.Matches(Kobzar.text);

        var dictionary = new Dictionary<string, int>();

        foreach (Match word in DzWords)
        {
            if (dictionary.ContainsKey(word.ToString()))
            {
                dictionary[word.ToString()]++;
            }
            else
            {
                dictionary[word.ToString()] = 1;
            }
        }

        var sortedDictionary = dictionary.OrderByDescending(pair => pair.Value);

        int count = 0;
        int toShow = 50;

        Console.WriteLine(" Всего символов: " + Kobzar.text.Length);
        Console.WriteLine(" Всего слов: " + allWords.Count);
        Console.WriteLine(" Слов от 3 до 20 символов:");

        Console.WriteLine("+---+------+----+");
        foreach (var pair in sortedDictionary)
        {
            Console.WriteLine($"| {count+1} | {pair.Key} | {pair.Value} |");
            count++;
            if (count == toShow)
            {
                break;
            }
        }
        Console.WriteLine("+---+------+----+");
    }
}

